"use strict";(self.webpackChunkmy_documentation=self.webpackChunkmy_documentation||[]).push([[3227],{28453:(n,e,t)=>{t.d(e,{R:()=>d,x:()=>o});var i=t(96540);const a={},r=i.createContext(a);function d(n){const e=i.useContext(r);return i.useMemo((function(){return"function"==typeof n?n(e):{...e,...n}}),[e,n])}function o(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(a):n.components||a:d(n.components),i.createElement(r.Provider,{value:e},n.children)}},76837:(n,e,t)=>{t.r(e),t.d(e,{assets:()=>u,contentTitle:()=>o,default:()=>m,frontMatter:()=>d,metadata:()=>i,toc:()=>b});const i=JSON.parse('{"id":"DB/ProjectDB/projektdb06-innerjoin2","title":"INNER JOIN II","description":"","source":"@site/docs/07-DB/03-ProjectDB/projektdb06-innerjoin2.md","sourceDirName":"07-DB/03-ProjectDB","slug":"/DB/ProjectDB/projektdb06-innerjoin2","permalink":"/my-documentation/docs/DB/ProjectDB/projektdb06-innerjoin2","draft":false,"unlisted":false,"editUrl":"https://github.com/vascoalexander/my-documentation/tree/main/docs/07-DB/03-ProjectDB/projektdb06-innerjoin2.md","tags":[],"version":"current","sidebarPosition":13,"frontMatter":{"sidebar_position":13,"description":"","draft":false,"title":"INNER JOIN II"},"sidebar":"dbSidebar","previous":{"title":"INNER JOIN I","permalink":"/my-documentation/docs/DB/ProjectDB/projektdb06-innerjoin1"},"next":{"title":"SELF JOIN","permalink":"/my-documentation/docs/DB/ProjectDB/projektdb07-selfjoin"}}');var a=t(74848),r=t(28453);const d={sidebar_position:13,description:"",draft:!1,title:"INNER JOIN II"},o=void 0,u={},b=[];function c(n){const e={code:"code",pre:"pre",...(0,r.R)(),...n.components};return(0,a.jsx)(e.pre,{children:(0,a.jsx)(e.code,{className:"language-sql",children:"-- Nutzen Sie die ProjektDB-Datenbank, um die folgenden\n-- Aufgaben zu l\xf6sen\n\n\n-- Aufgabe 6.9\n--\n-- Nennen Sie einmalig die Namen der Projekte, in denen die \n-- Mitarbeiter arbeiten, die ein Gehalt von mindestens \n-- 5.000 \u20ac beziehen.\n--\n--      pr_name\n--      Apollo\n--      Ariane\n--      Gemini\n\nSELECT DISTINCT bezeichnung\nFROM Projekt p\nINNER JOIN Arbeit a ON p.id = a.pro_id\nINNER JOIN Mitarbeiter m ON m.id = a.mit_id\nINNER JOIN Gehalt g ON m.id = g.mit_id\nWHERE g.gehalt >= 5000;\n\n-- Aufgabe 6.10\n--\n-- Erstellen Sie das Kartesische Produkt auf Mitarbeiter- und Abteilungs-Tabelle\n--\n--      id     nachname  vorname   abt_id  stadt       id  bezeichnung  stadt\n--      2581   Kaufmann  Brigitte  a2      NULL        a1  Beratung     M\xfcnchen\n--      5765   Sch\xe4fer   Sabine    a3      Landshut    a1  Beratung     M\xfcnchen\n--      9031   Meier     Rainer    a2      NULL        a1  Beratung     M\xfcnchen\n--      9912   Wolf      Klaus     a4      Heidenheim  a1  Beratung     M\xfcnchen\n--      10102  Huber     Petra     a3      Landshut    a1  Beratung     M\xfcnchen\n--      12121  Richter   Ursula    a4      M\xfcnchen     a1  Beratung     M\xfcnchen\n--      ...\n--      (75 Zeilen)\n\nSELECT *\nFROM Mitarbeiter m, Abteilung a\n\n-- Aufgabe 6.11\n--\n-- Finden Sie alle Mitarbeiter und dazu alle Abteilungen, in denen \n-- diese Mitarbeiter NICHT arbeiten.\n--\n--      id     nachname  vorname   abt_id  stadt       id  bezeichnung  stadt\n--      2581   Kaufmann  Brigitte  a2      NULL        a1  Beratung     M\xfcnchen\n--      5765   Sch\xe4fer   Sabine    a3      Landshut    a1  Beratung     M\xfcnchen\n--      9031   Meier     Rainer    a2      NULL        a1  Beratung     M\xfcnchen\n--      9912   Wolf      Klaus     a4      Heidenheim  a1  Beratung     M\xfcnchen\n--      10102  Huber     Petra     a3      Landshut    a1  Beratung     M\xfcnchen\n--      12121  Richter   Ursula    a4      M\xfcnchen     a1  Beratung     M\xfcnchen\n--      ...\n--      (60 Zeilen)\n\nSELECT *\nFROM Mitarbeiter m, Abteilung a\nWHERE m.abt_id <> a.id;\n\n-- Aufgabe 6.12\n--\n-- Nennen Sie die Abteilungsnamen der Mitarbeiter, die \n-- am 01.01.2019 eingestellt wurden.\n--\n--\t\tbezeichnung\n--\t\tFreigabe\n--\t\tEinkauf\n\nSELECT bezeichnung\nFROM Abteilung a\nINNER JOIN Mitarbeiter m ON m.abt_id = a.id\nINNER JOIN Arbeit ab ON ab.mit_id = m.id\nWHERE ab.einst_dat = '20190101';\n\n-- Aufgabe 6.13\n--\n-- Nennen Sie Namen und Vornamen aller Projektleiter, deren \n-- Abteilung den Standort Stuttgart hat.\n--\n--      nachname  vorname\n--      Sch\xe4fer   Sabine\n--      Huber     Petra\n\nSELECT m.nachname, m.vorname\nFROM Mitarbeiter m\nINNER JOIN Arbeit a ON m.id = a.mit_id\nINNER JOIN Abteilung ab ON m.abt_id = ab.id\nWHERE a.aufgabe = 'Projektleiter' AND ab.stadt = 'Stuttgart';\n\n-- Aufgabe 6.14\n--\n-- Nennen Sie einmalig die Namen der Projekte, in denen \n-- Mitarbeiter arbeiten, die zur Abteilung Beratung geh\xf6ren.\n--\n--      bezeichnung\n--      Apollo\n--      Gemini\n\nSELECT DISTINCT p.bezeichnung\nFROM Projekt p\nINNER JOIN Arbeit a ON a.pro_id = p.id\nINNER JOIN Mitarbeiter m ON m.id = a.mit_id\nINNER JOIN Abteilung ab ON ab.id = m.abt_id\nWHERE ab.bezeichnung = 'Beratung';\n\n-- Aufgabe 6.15\n--\n-- Nennen Sie die Kunden, an deren Projekten Mitarbeiter\n-- arbeiten, die mindestens 5.000 \u20ac Gehalt bekommen. Nennen\n-- Sie zu den Kunden auch die Anzahl dieser Mitarbeiter.\n--\n--      firma                    mitarbeiter\n--      Finanzamt Ulm            2\n--      Frankreich-Reisen GmbH   2\n--      Technische Produkte oHG  1\n\nSELECT k.firma, COUNT(m.id) as mitarbeiter\nFROM Kunde k\nINNER JOIN Projekt p ON p.kd_id = k.id\nINNER JOIN Arbeit a ON a.pro_id = p.id\nINNER JOIN Mitarbeiter m ON m.id = a.mit_id\nINNER JOIN Gehalt g ON g.mit_id = m.id\nWHERE g.gehalt >= 5000\nGROUP BY firma\n"})})}function m(n={}){const{wrapper:e}={...(0,r.R)(),...n.components};return e?(0,a.jsx)(e,{...n,children:(0,a.jsx)(c,{...n})}):c(n)}}}]);